import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CertificateData } from "./CertificateData";

export default function Certificate() {
  const [certificate, setCertificate] = useState(null);
  const { certificate_id } = params

  useEffect(() => {
    console.log(CertificateData);
    console.log(certificate_id)
    const selectedCertificate = CertificateData.find(
      (cert) => cert.certificate_id === parseInt(certificate_id)
    );
    setCertificate(selectedCertificate);
  }, [certificate_id]);

  if (!certificate) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>
        Certificate of {certificate.name}
      </h1>
      <p>Course: {certificate.course}</p>
      <p>Center: {certificate.center}</p>
    </div>
  );
}
