import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export default function Certificate() {
    const params = useParams();
    const { certificate_id } = params;
    const [studentData, setStudentData] = useState(null);

    useEffect(() => {
        fetch("/path/to/CertificateJSON.json") // Update the path to your JSON file
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                return response.json();
            })
            .then((data) => {
                const student = data.find((item) => item.certificate_id === parseInt(certificate_id));
                setStudentData(student);
            })
            .catch((error) => console.error("Error loading student data: ", error));
    }, [certificate_id]);

    if (!studentData) {
        return <div>Loading...</div>;
    }

    const { name, course, center } = studentData;

    return (
        <div>
            <h1>Certificate of {name}</h1>
            <p>Course: {course}</p>
            <p>Center: {center}</p>
        </div>
    );
}
